{
  "settings": {
    "index": {
      "max_ngram_diff": 3
    },
    "analysis": {
      "char_filter": {
        "replace_specials": {
          "type": "mapping",
          "mappings": [
            "& => and",
            "- => ",
            "_ => ",
            "'s => s",
            "'d => d",
            "v.s. => versus",
            "vs => versus",
            "ep. => episode"
          ]
        }
      },
      "filter": {
        "ngram_filter": {
          "type": "ngram",
          "min_gram": 2,
          "max_gram": 3,
          "token_chars": ["letter", "digit"]
        },
        "synonym_filter": {
          "type": "synonym",
          "synonyms": [
            "i => 1",
            "ii => 2",
            "iii => 3",
            "iv => 4",
            "v => 5",
            "vi => 6",
            "vii => 7",
            "viii => 8",
            "ix => 9",
            "x => 10",
            "xi => 11",
            "xii => 12",
            "xiii => 13",
            "xiv => 14",
            "xv => 15",
            "xvi => 16",
            "xvii => 17",
            "xviii => 18",
            "xix => 19",
            "xx => 20"
          ]
        }
      },
      "analyzer": {
        "ngram_analyzer": {
          "type": "custom",
          "char_filter": [
            "replace_specials"
          ],
          "tokenizer": "standard",
          "filter": [
            "lowercase",
            "asciifolding",
            "synonym_filter",
            "ngram_filter"
          ]
        },
        "whitespace_analyzer": {
          "type": "custom",
          "char_filter": [
            "replace_specials"
          ],
          "tokenizer": "whitespace",
          "filter": [
            "lowercase",
            "asciifolding",
            "synonym_filter"
          ]
        }
      }
    }
  }
}

